text.script:
if (isPasswordMode == 1) {
	if(inputTextCount > 0) {
		inputPassword="";
		counter = 0;
		while(1) {
			if(counter >= inputTextCount)
				break;
			inputPassword += "*";
			counter += 1;
		}
		if (PHinputTextCount > 0)
			print(inputPassword + "_  {" + inputCodeStr + ": " + PHinputText + "_}");
		else
			print(inputPassword + "_");
	}
	else {
		print("_");
	}
}
else {
	if (PHinputTextCount > 0)
		print(inputText + "_  {" + inputCodeStr + ": " + PHinputText + "_}");
	else
		print(inputText + "_");
}

if (isOnline == 1) {
	"※ 線上版 ※";
}
else {
	"※ 本機版 ※";
}

onUserInput.script:
			specialMsg = "";

			ret = "false";
			inputNumber = null;
			userInput = currentUserInput();

			if (userInput == "one" || userInput == "1")
				inputNumber = "1";
			else if (userInput == "two" || userInput == "2")
				inputNumber = "2";
			else if (userInput == "three" || userInput == "3")
				inputNumber = "3";
			else if (userInput == "four" || userInput == "4")
				inputNumber = "4";
			else if (userInput == "five" || userInput == "5")
				inputNumber = "5";
			else if (userInput == "six" || userInput == "6")
				inputNumber = "6";
			else if (userInput == "seven" || userInput == "7")
				inputNumber = "7";
			else if (userInput == "eight" || userInput == "8")
				inputNumber = "8";
			else if (userInput == "nine" || userInput == "9")
				inputNumber = "9";
			else if (userInput == "zero" || userInput == "0")
				inputNumber = "0";
			else if (userInput == "pagedown")
				inputNumber = " ";
			else if (userInput == "video_repeat")
				inputNumber = lastItemTitle;
			else if (userInput == "pageup") {
				firstUse = 0;

				if (currentPage == 0) {
					currentPage    = prevPage;
					itemTitleArray = prevItemTitleArray;
					inputCodeStr   = prevInputCodeStr;
					inputModeStr   = prevInputModeStr;
					inputMethod    = prevInputMethod;
					/* Fixed itemSize for all the static pages */
					itemSize       = 48;
				}

				if (PHinputTextCount > 0) {
					PHinputTextCount -= 1;
					PHinputTextArray = deleteStringArrayAt(PHinputTextArray, PHinputTextCount);
					PHinputText = "";
					counter = 0;
					while(1) {
						if(counter >= PHinputTextCount)
							break;
						PHinputText += getStringArrayAt(PHinputTextArray, counter);
						counter += 1;
					}
					redrawDisplay();
				}
				else if (inputTextCount > 0) {
					inputTextCount -= 1;
					inputTextArray = deleteStringArrayAt(inputTextArray, inputTextCount);
					inputText = "";
					counter = 0;
					while(1) {
						if(counter >= inputTextCount)
							break;
						inputText += getStringArrayAt(inputTextArray, counter);
						counter += 1;
					}
					redrawDisplay();
				}
				ret = "true";
			}
			else if(userInput == "video_play") {
				if (isPasswordMode != 1) {
					saveString = null;
					saveString = pushBackStringArray(saveString, inputTextCount);
					saveString = pushBackStringArray(saveString, inputTextArray);
					writeStringToFile(getStoragePath("tmp")+"ypKeyboar.saveString.dat", saveString);
				}
				setReturnString(inputText);
				postMessage("return");
				ret = "true";
			}
			else if (userInput == "video_search") {
				PHinputTextCount = 0;
				PHinputTextArray = null;
				PHinputText = "";

				if (currentPage == 1) {
					currentPage    = 2;
					itemTitleArray = itemTitleArrayP2;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "英文";
					inputModeStr    = "大寫";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}
				else if (currentPage == 2) {
					currentPage    = 3;
					itemTitleArray = itemTitleArrayP3;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "注音";
					inputModeStr    = "簡體";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}
				else if (currentPage == 3) {
					currentPage    = 4;
					itemTitleArray = itemTitleArrayP4;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "注音";
					inputModeStr    = "正體";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}
				else if (currentPage == 4) {
					currentPage    = 5;
					itemTitleArray = itemTitleArrayP5;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "速成";
					inputModeStr    = "簡體";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}
				else if (currentPage == 5) {
					currentPage    = 6;
					itemTitleArray = itemTitleArrayP6;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "速成";
					inputModeStr    = "正體";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}
				else if (currentPage == 6) {
					currentPage    = 1;
					itemTitleArray = itemTitleArrayP1;
					/* Fixed itemSize for all the static pages */
					itemSize        = 48;
					inputCodeStr    = "英文";
					inputModeStr    = "小寫";
					inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
				}

				redrawDisplay();
				ret = "true";
			}
			else if ((userInput == "display") && (PHinputTextCount > 0)) {

				/* input methods start from page 3 */
				prefixIndex = Minus(currentPage, 3);
				fileChar = allFilePrefix + getStringArrayAt(fileCharPrefix, prefixIndex) + fileCharInfix + "i=" + PHinputText + fileExt;
				itemTitleArrayChar = readStringFromFile(fileChar);
				if (((itemTitleArrayChar == null) || (itemTitleArrayChar == "")) && (isOnline == 1)) {
					dldUrl2File(allFileRemote + getStringArrayAt(fileCharPrefix, prefixIndex) + fileCharRemote + "i=" + urlEncode(PHinputText) + fileExt, fileChar);
					itemTitleArrayChar = readStringFromFile(fileChar);
				}

				if ((itemTitleArrayChar != null) && (itemTitleArrayChar != "")) {
					prevPage           = currentPage;
					prevItemTitleArray = itemTitleArray;
					prevInputCodeStr   = inputCodeStr;
					prevInputModeStr   = inputModeStr;
					prevInputMethod    = inputMethod;
					currentPage        = 0;
					itemTitleArray     = itemTitleArrayChar;
					itemSize           = getStringArrayAt(itemTitleArray, 0);
				}
				else {
					specialMsg = " -- 無此字";
				}
				redrawDisplay();
				ret = "true";
			}

			if (inputNumber != null) {
				if(firstUse == 1) {
					if(inputNumber == " ")
						inputNumber = "";
					else {
						inputText = "";
						inputTextArray = null;
						inputTextCount = 0;
					}
					firstUse = 0;
					redrawDisplay();
				}
				if(inputNumber != "") {
					inputText += inputNumber;
					inputTextArray  = pushBackStringArray(inputTextArray, inputNumber);
					inputTextCount -= -1;
					redrawDisplay();
					ret = "true";
				}
			}
			ret;

rss.onEnter:
	/* if this module is used for input passwords, set it to 1 */
	isPasswordMode = 0;

	/* if this module is used online, set it to 1 and set allFileRemote to an appropriate URL prefix */
	isOnline = 0;
	if (isOnline == 1) {
		allFileRemote = "http://input.zzl.org/getFile.php?file=";
		fileCharRemote = "/";

		allFilePrefix = "/usr/local/etc/dvdplayer/";
		fileCharInfix = "_";
	}
	else {
		allFilePrefix = "./etc/ypInput/";
		fileCharInfix = "/";
	}

	fileExt = ".txt";

	specialMsg = "";

	screenXp = 4;
	screenYp = 3;

	fileCharPrefix = null;
	fileCharPrefix = pushBackStringArray(fileCharPrefix, "ypPhoneticCHS");
	fileCharPrefix = pushBackStringArray(fileCharPrefix, "ypPhoneticCHT");
	fileCharPrefix = pushBackStringArray(fileCharPrefix, "ypCJFastCHS");
	fileCharPrefix = pushBackStringArray(fileCharPrefix, "ypCJFastCHT");

	dictPages = null;
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p1" + fileExt);
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p2" + fileExt);
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p3" + fileExt);
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p4" + fileExt);
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p5" + fileExt);
	dictPages = pushBackStringArray(dictPages, "ypDictionary_p6" + fileExt);

	page = 0;
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP1 = readStringFromFile(filePage);
	if ((itemTitleArrayP1 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP1 = readStringFromFile(filePage);
	}
	page = Add(page, 1);
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP2 = readStringFromFile(filePage);
	if ((itemTitleArrayP2 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP2 = readStringFromFile(filePage);
	}
	page = Add(page, 1);
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP3 = readStringFromFile(filePage);
	if ((itemTitleArrayP3 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP3 = readStringFromFile(filePage);
	}
	page = Add(page, 1);
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP4 = readStringFromFile(filePage);
	if ((itemTitleArrayP4 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP4 = readStringFromFile(filePage);
	}
	page = Add(page, 1);
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP5 = readStringFromFile(filePage);
	if ((itemTitleArrayP5 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP5 = readStringFromFile(filePage);
	}
	page = Add(page, 1);
	filePage = allFilePrefix + getStringArrayAt(dictPages, page);
	itemTitleArrayP6 = readStringFromFile(filePage);
	if ((itemTitleArrayP6 == null) && (isOnline == 1)) {
		dldUrl2File(allFileRemote + getStringArrayAt(dictPages, page), filePage);
		itemTitleArrayP6 = readStringFromFile(filePage);
	}
	page = Add(page, 1);

	prevPage           = 0;
	prevItemTitleArray = null;
	prevInputCodeStr   = "";
	prevInputModeStr   = "";
	prevInputMethod    = "";

	currentPage        = 1;
	itemTitleArray     = itemTitleArrayP1;
	/* Fixed itemSize for all the static pages */
	itemSize           = 48;
	inputCodeStr       = "英文";
	inputModeStr       = "小寫";
	inputMethod        = " [" + inputCodeStr + inputModeStr + "]";

	lastItemTitle = "";

	inputTextCount = 0;
	inputTextArray = null;
	inputText = "";

	PHinputTextCount = 0;
	PHinputTextArray = null;
	PHinputText = "";

	saveString=null;
	if(isPasswordMode != 1){
		saveString = readStringFromFile(getStoragePath("tmp")+"ypKeyboar.saveString.dat");
		if(saveString != null) {
			inputTextCount = getStringArrayAt(saveString, 0);
			counter = 0;
			while(1) {
				if(counter >= inputTextCount)
					break;
				ch = getStringArrayAt(saveString, counter+1);
				inputText += ch;
				inputTextArray = pushBackStringArray(inputTextArray, ch);
				counter += 1;
			}
		}
	}

	firstUse = 0;
	if(inputText != "") firstUse = 1;

	setFocusItemIndex(0);
	redrawDisplay();

item_template.onClick:
		idx = getFocusItemIndex();
		itemTitle = getStringArrayAt(itemTitleArray, idx-(-1));

		if ((currentPage != 0) && (itemTitle == "切")) {
			PHinputTextCount = 0;
			PHinputTextArray = null;
			PHinputText = "";

			if (currentPage == 1) {
				currentPage    = 2;
				itemTitleArray = itemTitleArrayP2;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "英文";
				inputModeStr    = "大寫";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
			else if (currentPage == 2) {
				currentPage    = 3;
				itemTitleArray = itemTitleArrayP3;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "注音";
				inputModeStr    = "簡體";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
			else if (currentPage == 3) {
				currentPage    = 4;
				itemTitleArray = itemTitleArrayP4;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "注音";
				inputModeStr    = "正體";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
			else if (currentPage == 4) {
				currentPage    = 5;
				itemTitleArray = itemTitleArrayP5;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "速成";
				inputModeStr    = "簡體";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
			else if (currentPage == 5) {
				currentPage    = 6;
				itemTitleArray = itemTitleArrayP6;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "速成";
				inputModeStr    = "正體";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
			else if (currentPage == 6) {
				currentPage    = 1;
				itemTitleArray = itemTitleArrayP1;
				/* Fixed itemSize for all the static pages */
				itemSize        = 48;
				inputCodeStr    = "英文";
				inputModeStr    = "小寫";
				inputMethod     = " [" + inputCodeStr + inputModeStr + "]";
			}
		}
		else {
			if (firstUse == 1) {
				inputText = "";
				inputTextArray = null;
				inputTextCount = 0;
				firstUse = 0;
			}

			if (currentPage < 3) {
				inputText += itemTitle;
				inputTextArray  = pushBackStringArray(inputTextArray, itemTitle);
				inputTextCount -= -1;

				lastItemTitle = itemTitle;
			}
			else if ((itemTitle != "") && (itemTitle != "Go")) {
				PHinputText += itemTitle;
				PHinputTextArray  = pushBackStringArray(PHinputTextArray, itemTitle);
				PHinputTextCount -= -1;
			}
		}

		if (currentPage == 0) {
			currentPage    = prevPage;
			itemTitleArray = prevItemTitleArray;
			inputCodeStr   = prevInputCodeStr;
			inputModeStr   = prevInputModeStr;
			inputMethod    = prevInputMethod;
			/* Fixed itemSize for all the static pages */
			itemSize       = 48;

			PHinputTextCount = 0;
			PHinputTextArray = null;
			PHinputText = "";
		}
		else if (
			(((currentPage == 3) || (currentPage == 4)) && (PHinputTextCount > 0) &&
			((itemTitle == "Go") || (itemTitle == "ˇ") || (itemTitle == "ˋ") || (itemTitle == "ˊ") || (itemTitle == "˙")))
			||
			(((currentPage == 5) || (currentPage == 6)) &&
			((PHinputTextCount == 2) || ((PHinputTextCount > 0) && (itemTitle == "Go"))))
			) {

			/* input methods start from page 3 */
			prefixIndex = Minus(currentPage, 3);
			fileChar = allFilePrefix + getStringArrayAt(fileCharPrefix, prefixIndex) + fileCharInfix + "i=" + PHinputText + fileExt;
			itemTitleArrayChar = readStringFromFile(fileChar);
			if (((itemTitleArrayChar == null) || (itemTitleArrayChar == "")) && (isOnline == 1)) {
				dldUrl2File(allFileRemote + getStringArrayAt(fileCharPrefix, prefixIndex) + fileCharRemote + "i=" + urlEncode(PHinputText) + fileExt, fileChar);
				itemTitleArrayChar = readStringFromFile(fileChar);
			}

			if ((itemTitleArrayChar != null) && (itemTitleArrayChar != "")) {
				prevPage           = currentPage;
				prevItemTitleArray = itemTitleArray;
				prevInputCodeStr   = inputCodeStr;
				prevInputModeStr   = inputModeStr;
				prevInputMethod    = inputMethod;
				currentPage        = 0;
				itemTitleArray     = itemTitleArrayChar;
				itemSize           = getStringArrayAt(itemTitleArray, 0);
			}
			else {
				specialMsg = " -- 無此字";
			}
		}

		redrawDisplay();
		null;

title.script:
if (isPasswordMode == 1)
	"密碼輸入";
else
	"字串輸入";
